mapper.update:
        insert into van (vanNum, vanIP, vanName)
        values(#{vanNum},#{vanIP},#{vanName})
		on duplicate key update vanNum=#{vanNum}, vanIP=#{vanIP}, vanName=#{vanName}


		insert into ext_dev (devName, devType, prtcl)
        values(#{devName},#{devType},#{prtcl})
		on duplicate key update devName=#{devName}, devType=#{devType}, prtcl=#{prtcl}

        insert into goods_cat (goodsCatNum, goodsCatName, goodsCatLoc)
        values(#{goodsCatNum},#{goodsCatName},#{goodsCatLoc})
		on duplicate key update goodsCatNum=#{goodsCatNum}, goodsCatName=#{goodsCatName}, goodsCatLoc=#{goodsCatLoc}

        insert into table_cat (tableCatNum, tableCatName, tableCatLoc)
        values(#{tableCatNum},#{tableCatName},#{tableCatLoc})
		on duplicate key update tableCatNum=#{tableCatNum}, tableCatName=#{tableCatName}, tableCatLoc=#{tableCatLoc}

		insert into emp (posNum, empId, empName, pwd)
        values(#{posNum},#{empId},#{empName},#{pwd})
		on duplicate key update empId=#{empId}, posNum=#{posNum}, empName=#{empName}, pwd=#{pwd}

		insert into open(openDay, empId, posNum, cashAmnt)
        values(#{openDay},#{empId},#{posNum},#{cashAmnt})
		on duplicate key update openDay=#{openDay}, empId=#{empId}, posNum=#{posNum}, cashAmnt=#{cashAmnt}

		insert into print (printNum, devName, printName, printCntt)
        values(#{printNum},#{devName},#{printName},#{printCntt})
		on duplicate key update printNum=#{printNum}, devName=#{devName}, printName=#{printName}, printCntt=#{printCntt}

		insert into order_goods (orderGoodsNum, goodsNum, orderNum, goodsQntt, openDay)
        values(#{orderGoodsNum},#{goodsNum},#{orderNum},#{goodsQntt},#{openDay})
		on duplicate key update orderGoodsNum=#{orderGoodsNum}, goodsNum=#{goodsNum}, orderNum=#{orderNum},goodsQntt=#{goodsQntt}, openDay=#{openDay}
